import React, { useEffect, useRef } from 'react';
import { View, StyleSheet, Animated } from 'react-native';
import { rhp } from '../constants/dimensions';

const ProgressBar = ({ progress, style }) => {
  const animatedWidth = useRef(new Animated.Value(0)).current;
  const prevProgress = useRef(progress); // Store the previous progress value

  useEffect(() => {
    // Only animate if the progress changes
    if (progress !== prevProgress.current) {
      Animated.timing(animatedWidth, {
        toValue: progress, // Animate to the new progress value
        duration: 1000,
        useNativeDriver: false,
      }).start();
      prevProgress.current = progress; // Update the previous progress
    }
  }, [progress]); // Only trigger animation when progress changes

  return (
    <View style={styles.barBackground}>
      <Animated.View
        style={[
          styles.barFront,
          styles.barProgress(animatedWidth),
          style,
        ]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  barBackground: {
    marginTop: rhp(12),
    height: rhp(4),
    backgroundColor: '#EFEFEF',
    borderRadius: 8,
  },
  barFront: {
    height: rhp(4),
    borderTopLeftRadius: 8,
    borderBottomLeftRadius: 8,
    backgroundColor: '#4681AA',
  },
  barProgress: (animatedWidth) => ({
    width: animatedWidth.interpolate({
      inputRange: [0, 100],
      outputRange: ['0%', '100%'],
    }),
    borderTopRightRadius: animatedWidth._value === 100 ? 8 : 0,
    borderBottomRightRadius: animatedWidth._value === 100 ? 8 : 0,
  }),
});

export default ProgressBar;
